import {useEffect, useState} from "react";
import {BaseUrl} from "../../../BaseUrl";

export default function Commission() {
    const [last,setLast] = useState("");
    useEffect(() => {
        fetch(BaseUrl+"enchere/admin/commission/last")
            .then((response) => response.json())
            .then((data) => {
                setLast(data.data);
            })
            .catch((error) => {
                console.error(error);
            });
    }, [urls]);
    const handleSubmit = (e) => {
        e.preventDefault();
        const formData = e.target;
        const tough = formData.elements.namedItem('taux').value;
        let data = {
            "taux":tough
        };
        if (tough==="")
            alert("veuillez remplir ce champ");
        else{
        alert(data.taux)
        }
    }
    return (
        <>
            <div className="container">
                <div className="row col-md-12">
                    <form onSubmit={handleSubmit}>
                        <h1>modifier commission</h1>
                        <div className="row">
                            <div className="col-md-6">
                                <p>last commission : {last?.taux} %</p>
                                <div className="form-group">
                                    <label htmlFor="cat">default tough auction</label>
                                    <input type="number" min={0} name="taux" /> %
                                </div>
                            </div>
                        </div><br/>

                        <div className="row">
                            <div className="col-md-6">
                                <div className="form-group">
                                    <button type="submit" className="btn btn-primary" >Valider</button>
                                </div>
                            </div>
                        </div>
                    </form>
                </div>
            </div>
        </>
    );
}
