import {useState} from "react";

function Login(props) {
    const user = (props.action===0)?"client":"admin";
    const link = (props.action===0)?"http://localhost:8000/enchere/cli/login":"http://localhost:8000/enchere/admin/login";
    let d_email = (props.action===0)?"myranto@gmail.com":"kely@gmail.com";
    let d_mdp = (props.action===0)?"manantsoa02":"randria";
    const [email,setEmail] = useState(null);
    const [mdp,setMdp] = useState(null);
    const handleChange = (e) =>{
        setEmail(e.target.value)
    }
    const handleSubmit = (e) => {
        e.preventDefault();
        const formData = e.target;
        const json = {
            "email": formData.elements.namedItem('email').value,
            "mdp": formData.elements.namedItem('pwd').value
        };
        // console.log(json);
        fetch(
            link,
            {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(json),
            }
        )
            .then((response) => response.json())
            .then((res) => {
                // alert(res.data)
                console.log(res)
                // window.location.href = "/request-profil";
            })
            .catch((error) => {
                console.error(error);
                //TODO implement error
            });
    }
    return (
        <>
            <div className="container">
                <h1>se connecter en tant que {user}</h1>
                <div className="row col-md-12">
                    <form onSubmit={handleSubmit}>
                        <h1 className="text-center">Login</h1>
                        <div className="row">
                            <div className="col-md-6">
                                <div className="form-group">
                                    <label htmlFor="email">Email</label>
                                    <input type="email" name="email" value={d_email} onChange={handleChange} className="form-control"></input><br/>
                                </div>
                            </div>
                            <div className="col-md-6">
                                <div className="form-group">
                                    <label htmlFor="pwd">Mot de passe</label>
                                    <input type="password" name="pwd" value={d_mdp} onChange={handleChange} className="form-control"></input><br/>
                                </div>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-md-6">
                                <div className="form-group">
                                    <button type="submit" className="btn btn-primary" >Valider</button>
                                </div>
                            </div>
                        </div>
                    </form>
                </div>
            </div>

        </>
    );
}



export default Login;
