                    headers: {
                        "Content-Type": "application/json",
                    },
                    body: JSON.stringify(data),
                }
            )
                .then((response) => response.json())
                .then((res) => {
                    alert(res.data)
                     window.location.href = "/indexAdmin";
                })
                .catch((error) => {
                    console.error(error);
