function FormCategorie() {
    const handleSubmit=(e)=>{
        e.preventDefault();
        const formData = e.target;
        const categorie = formData.elements.namedItem('categorie').value;
        let data = {
            "nom":categorie
        };
        fetch(
            "http://localhost:8000/enchere/admin/categorie",
            {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(data),
            }
        )
            .then((response) => response.json())
            .then((data) => {
                console.log(data);
                window.location.href = "/request-profil";
            })
            .catch((error) => {
                console.error(error);
                //TODO implement error
            });
        console.log(data);
    }
    return (
        <>
        <div className="container">
            <div className="row col-md-12">
                <form onSubmit={handleSubmit}>
                    <h1>Ajout Catégorie</h1>
                    <div className="row">
                        <div className="col-md-6">
                            <div className="form-group">
                                <label htmlFor="cat">Catégorie</label>
                                <input type="text" name="categorie" />
                            </div>
                        </div>
                    </div><br/>

                    <div className="row">
                        <div className="col-md-6">
                            <div className="form-group">
                                <button type="submit" className="btn btn-primary" >Valider</button>
                            </div>
                        </div>
                    </div>
                </form>
            </div>
        </div>
    </>
    );
}

export default FormCategorie;
