import {useState} from "react";
const isClient = (props) =>{
        if (props.hasOwnProperty("token")){
            const detail = {
                "id":props.id,
                "token":props.token
            }
            localStorage.setItem("client",JSON.stringify(detail));
            window.location.href = "/indexCli";
        }else{
            window.location.href = "/indexAdmin";
        }
}
function Login(props) {
    const user = (props.action===0)?"client":"admin";
    const link = (props.action===0)?"http://localhost:8000/enchere/cli/login":"http://localhost:8000/enchere/admin/login";
    let d_email = (props.action===0)?"myranto@gmail.com":"kely@gmail.com";
    let d_mdp = (props.action===0)?"manantsoa02":"randria";
    const [email,setEmail] = useState(d_email);
    const [mdp,setMdp] = useState(d_mdp);
    const [response,setResponse] = useState("");
    const handleSubmit = (e) => {
        e.preventDefault();
        const json = {
            "email": email,
            "mdp": mdp
        };
        // console.log(json);
        fetch(
            link,
            {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(json),
            }
        )
            .then((response) => response.json())
            .then((res) => {
                // alert(res.data)
                if (res.error!==null){
                    setResponse(res.error)
                }else{
                    isClient(res.data);
                // window.location.href = "/request-profil";
                }
            })
            .catch((error) => {
                console.error(error);
                //TODO implement error
            });
    }
    return (
        <>
            <div className="container">
                <h1>se connecter en tant que {user}</h1>
                <div className="row col-md-12">
                    <form onSubmit={handleSubmit}>
                        <h1 className="text-center">Login</h1>
                        <div className="row">
                            <div className="col-md-6">
                                <div className="form-group">
                                    <label htmlFor="email">Email</label>
                                    <input type="email" name="email" value={email} onChange={(event) => setEmail(event.target.value)} className="form-control"></input><br/>
                                </div>
                            </div>
                            <div className="col-md-6">
                                <div className="form-group">
                                    <label htmlFor="pwd">Mot de passe</label>
                                    <input type="password" name="pwd" value={mdp} onChange={(event) => setMdp(event.target.value)} className="form-control"></input><br/>
                                </div>
                                <h1>{response}</h1>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-md-6">
                                <div className="form-group">
                                    <button type="submit" className="btn btn-primary" >Valider</button>
                                </div>
                            </div>
                        </div>
                    </form>
                </div>
            </div>

        </>
    );
}



export default Login;
